"async() => {
		const send = this.isMember ? http.delete : http.post;

		const res = await send<boolean>(joinClub(), {
			clubId: this.clubId,
		},{
			RequestVerificationToken: this.csrf,
		});
		if (res.isSuccess) {
			this.isMember = res.getValue();
		} else {
			log.warn(res.error);
		}
	}"

